mapper.select:
SELECT

  distinct

'true' as QUERYID,

from yunmu_order yo
LEFT JOIN yunmu_hourse yh
on yo.hourse_code=yh.id where 0=0

  AND yo.id LIKE '%${orderId}%'


  AND  yo.complete_time >= #{beginTime}


  AND yh.hourse_number LIKE '%${hourseNumber}%'


  AND yo.complete_time <= #{searchTimeEnd}


  AND yo.del_flag =#{delFlag}


  AND yo.order_status =#{orderStatus}


  AND yo.project_id =#{projectId}


  and yo.project_id in

    #{project}


ORDER BY create_time DESC

SELECT
IFNULL(SUM(yo.order_rec_amount),0) as orderRecAmountAll,
IFNULL(SUM(yo.order_act_amount),0) as orderActmountAll,
IFNULL(SUM(yo.order_count),0) as orderCountAll
from yunmu_order yo
LEFT JOIN yunmu_hourse yh
on yo.hourse_code=yh.id where 0=0

  AND yo.id LIKE '%${orderId}%'


  AND  yo.create_time >= #{beginTime}


  AND yh.hourse_number LIKE '%${hourseNumber}%'


  AND yo.create_time <= #{searchTimeEnd}


  AND yo.del_flag =#{delFlag}


  AND yo.order_status =#{orderStatus}


  and yo.project_id in

    #{project}

SELECT IFNULL(sum(orderer.order_rec_amount),0) as yesAmount
from yunmu_order orderer where orderer.del_flag=0 and order_status=10 and orderer.hourse_code in(
  SELECT hourse.id from yunmu_hourse hourse where hourse.del_flag=0

    AND hourse.owner_code = #{ownerId}

)

  AND  orderer.complete_time >= #{beginTime}


  AND orderer.complete_time <= #{endTime}

SELECT IFNULL(sum(orderer.order_act_amount),0) as yesAmount
from yunmu_order orderer where orderer.del_flag=0 and order_status=10 and orderer.hourse_code in(
SELECT hourse.id from yunmu_hourse hourse where hourse.del_flag=0

  AND hourse.owner_code = #{ownerId}

)

  AND  orderer.complete_time >= #{beginTime}


  AND orderer.complete_time <= #{endTime}

SELECT IFNULL(sum(order_count),0)  from yunmu_order orderer where orderer.del_flag = 0 and order_status=10 and orderer.hourse_code in(
  SELECT hourse.id from yunmu_hourse hourse where hourse.del_flag=0

    AND hourse.owner_code = #{ownerId}

)

  AND  orderer.complete_time >= #{beginTime}


  AND orderer.complete_time <= #{endTime}

SELECT mo.id as oId,IFNULL(mo.order_act_amount,0) AS incomeAll ,mo.complete_time as date
from yunmu_order mo
where mo.del_flag=0 and mo.hourse_code in(
  SELECT hourse.id from yunmu_hourse hourse where hourse.del_flag=0

    AND hourse.owner_code = #{ownerId}

)

  AND  mo.create_time >= #{beginTime}


  AND mo.create_time <= #{endTime}

select

  distinct

'true' as QUERYID,

from yunmu_order yo
where yo.del_flag=0 and yo.hourse_code in(
SELECT hourse.id from yunmu_hourse hourse where hourse.del_flag=0

  AND hourse.owner_code = #{ownerId}

)

  AND  yo.create_time >= #{beginTime}


  AND  yo.order_status >= #{orderStatus}


  AND yo.create_time <= #{endTime}

SELECT

  distinct

'true' as QUERYID,

FROM  yunmu_order yo
where yo.del_flag=0 and order_status=10 and yo.hourse_code in(
    SELECT id from yunmu_hourse where del_flag=0 and hourse_number=#{hourseNumber}
)

  AND  yo.complete_time >= #{beginTime}


  AND yo.complete_time <= #{endTime}

SELECT IFNULL(SUM(yo.order_rec_amount),0)
FROM  yunmu_order yo
where yo.del_flag=0 and order_status=10 and yo.hourse_code in(
SELECT id from yunmu_hourse where del_flag=0 and hourse_number=#{hourseNumber}
)

  AND  yo.complete_time >= #{beginTime}


  AND yo.complete_time <= #{endTime}

SELECT count(*)
FROM  yunmu_order yo
where yo.del_flag=0 and order_status=10 and yo.hourse_code in(
SELECT id from yunmu_hourse where del_flag=0 and hourse_number=#{hourseNumber}
)

  AND  yo.complete_time >= #{beginTime}


  AND yo.complete_time <= #{endTime}

SELECT IFNULL(SUM(yo.order_act_amount),0)
FROM  yunmu_order yo
where yo.del_flag=0 and order_status=10 and yo.hourse_code in(
SELECT id from yunmu_hourse where del_flag=0 and hourse_number=#{hourseNumber}
)

  AND  yo.complete_time >= #{beginTime}


  AND yo.complete_time <= #{endTime}

SELECT

  distinct

'true' as QUERYID,

from yunmu_order yo where yo.del_flag=0 and yo.hourse_code in(
  SELECT hourse.id from yunmu_hourse hourse where hourse.del_flag=0

    AND hourse.owner_code = #{ownerId}

)

  AND  yo.create_time >= #{beginTime}


  AND yo.create_time <= #{endTime}


  AND yo.order_status =#{orderStatus}

ORDER BY create_time DESC
